import React, { useState } from 'react';
import '../'
import searchIcon from '../assets/search-icon.png';
import { useNavigate } from 'react-router-dom';

const Topbar = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const handleSearch = () => {
    if (searchQuery) {
      navigate(`/search?query=${searchQuery}`);
    }
  };

  return (
    <header className="header">
      <div className="header-left">
        <img src={searchIcon} alt="Search Icon" onClick={handleSearch} className="search-icon" />
        <input
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
          className="header-searchbar"
        />
      </div>
      <div className="header-right">
        <div className="profile-container" onClick={() => navigate('/profile')}>
          <img src="profile-pic.png" alt="Profile" className="profile-pic" />
        </div>
        <div className="notification-icon" onClick={() => navigate('/notifications')}>
          <img src="notification-bell.png" alt="Notifications" />
        </div>
      </div>
    </header>
  );
};

export default Topbar;
